//Import the JXL libraries
import jxl.*
import jxl.write.*
import groovy.json.JsonSlurper
//Create a file instance
def excelFilePath = "d:\\TestData.xls"
File excelFile = new File(excelFilePath)
//Create workbook instance
Workbook workbook = Workbook.getWorkbook(excelFile)

//Load the sheet
jxl.Sheet sheet = workbook.getSheet("Login")
//I want to read the data in the sheet
int noOfRows = sheet.getRows()-1 //number of rows in the excel
log.info "noOfRows = $noOfRows"
//need to loop through the records and print data
def email,password
for(int rowIdx in 1..noOfRows){
	//read the contents of the cell.
	email = sheet.getCell(0,rowIdx).getContents() 
	password = sheet.getCell(1,rowIdx).getContents()
	log.info "Setting email=$email , pass = $password to test case properties"
	testRunner.testCase.setPropertyValue('email',email)
	testRunner.testCase.setPropertyValue('pass',password)
	Thread.sleep(2000)
	//execute the login operation dynamically through groovy
	testRunner.testCase.getTestStepByName('Login').run(testRunner,context)
	def slurper = new JsonSlurper()
	def response=context.expand ('${Login#Response}')
	def logindata= slurper.parseText(response)
	log.info logindata
	String status =logindata.httpStatus
	String userid= logindata.get("body").get("userId")
	String description=logindata.get("body").get("role").get("description")
	if (status== '200 OK')
	{
		log.info "Pass";
	}
	else 
	{
		log.info "Pass";
	}











}
//close it
workbook.close()

import groovy.json.JsonSlurper
def fileName = "log.txt"
def inputFile = new File("D:\\"+fileName)
inputFile.write("TestCase Started...\n")
testRunner.testCase.getTestStepByName('GetProductById').run(testRunner,context)
def slurper = new JsonSlurper()
	def response=context.expand ('${GetProductById#Response}')
	def productdata= slurper.parseText(response)
	if (productdata.httpStatus =='302 FOUND')
	inputFile.append("Product Exists...\n")		
	String categoryId =productdata.get("body").get("categoryId").get("id")
	String color =productdata.get("body").get("color")
	String createdOn= productdata.get("body").get("createdOn")
	String description=productdata.get("body").get("description")
	String discount= productdata.get("body").get("discount")
	String materialDescription=productdata.get("body").get("materialDescription")
	String name=productdata.get("body").get("name")
	String price=productdata.get("body").get("price")
	String updateOn=productdata.get("body").get("updateOn")
	String warranty=productdata.get("body").get("warranty")
	testRunner.testCase.setPropertyValue('categoryId',categoryId)
	testRunner.testCase.setPropertyValue('color',color)
	testRunner.testCase.setPropertyValue('createdOn',createdOn)
	testRunner.testCase.setPropertyValue('description',description)
	testRunner.testCase.setPropertyValue('discount',discount)
	testRunner.testCase.setPropertyValue('materialDescription',materialDescription)
	testRunner.testCase.setPropertyValue('name',name)
	testRunner.testCase.setPropertyValue('price',price)
	testRunner.testCase.setPropertyValue('updateOn',updateOn)
	testRunner.testCase.setPropertyValue('warranty',warranty)
	testRunner.testCase.getTestStepByName('AddProduct').run(testRunner,context)
	def slurper2 = new JsonSlurper()
	def response2=context.expand ('${AddProduct#Response}')
	def addproductdata= slurper2.parseText(response2)
	String status=addproductdata.httpStatus
	if(status=='201 CREATED' )
	inputFile.append("Product Added...")
	//getting newly created product id"
	String productId= addproductdata.get("body").get("productId")
	//writing to properties
	testRunner.testCase.setPropertyValue('AddedProductId',productId)
	//updating created product id
	testRunner.testCase.getTestStepByName('UpdateProduct').run(testRunner,context)
	def slurper3 = new JsonSlurper()
	def response3=context.expand ('${UpdateProduct#Response}')
	def updateddata= slurper3.parseText(response3)
	String status1=updateddata.httpStatus
	if (status1 =='200 OK')
	inputFile.append("Product Updated...")
	String newproductId= updateddata.get("body").get("productId")
	testRunner.testCase.setPropertyValue('UpdatedProductId',newproductId)
	//deleting product	
	testRunner.testCase.getTestStepByName('DeleteProduct').run(testRunner,context)
	//validating deletion
	def slurper4 = new JsonSlurper()
	def response4=context.expand ('${DeleteProduct#Response}')
	def deleteddata= slurper4.parseText(response4)
	String status2=deleteddata.httpStatus
	if (status2 =='200 OK')
	inputFile.append("Product Deleted...")
